import { useRef } from "react"
import { imageClick } from "../sideBarsFunctions"
import image from '../../../../assests/The Coupon Project Logo.png'
import '../sidebar.css'

export default function AdminSidebar(props) {
    const buttons = { home: useRef(), test: useRef() }
    const buttonDivs = { home: useRef(), test: useRef() }

    const buttonClick = (event) => {
        for (let button of Object.values(buttons)) {
            button.current.className = 'Sidebar-button'
        }
        for (let div of Object.values(buttonDivs)) {
            div.current.className = 'Sidebar-button-div'
        }
        switch (event.target.name) {
            case 'Home':
                buttonDivs.home.current.className = 'Sidebar-button-div-clicked'
                buttons.home.current.className = 'Sidebar-button-clicked'
                props.setSendToContent('Home')
                break;
            case 'Test':
                buttonDivs.test.current.className = 'Sidebar-button-div-clicked'
                buttons.test.current.className = 'Sidebar-button-clicked'
                props.setSendToContent('Test')
                break;
            default:
                window.location.reload()
                break;
        }
    }


    return (<div className='AdminSidebar'>
        <img src={image} alt='logo' width='144' className='Sidebar-img' onClick={() => imageClick()} />
        <div className='Sidebar-img-div' onClick={() => imageClick()}></div>

        <div className='Siderbar-div-seperator'></div>
        <div className='Sidebar-button-div-clicked' ref={buttonDivs.home}>
            <button className='Sidebar-button-clicked' name='Home' ref={buttons.home} onClick={buttonClick} >Home Page</button>
        </div>

        <div className='Siderbar-div-button-seperator'></div>
        <div className='Sidebar-button-div' ref={buttonDivs.test}>
            <button className='Sidebar-button' name='Test' ref={buttons.test} onClick={buttonClick}>Temp</button>
        </div>
    </div>)
}
