import '../../../../../../../styles/action-sidebar.css'
import CustomerService from '../../../../../../../services/user-services/CustomerService'
import './CustomerCouponActionbar.css'

export default function CustomerCouponActionbar(props) {

    const handleAllCoupons = () => {
        CustomerService
            .getCoupons().then(
                result => {
                    props.setCoupons(result.data)
                },
                error => {
                    if (error.response.data.response) {
                        setTimeout(() => { alert("Action Failed\n" + error.response.data.response) }, 0)
                        return
                    }
                    if (error.response)
                        return
                    setTimeout(() => { alert("Servers are currently down, try again later.") }, 0)
                }
            )
    }

    const handleSearchCategory = () => {
        let userOption = prompt("Attraction\nAutomobile\nElectricity\nFood\nGaming\nRestaurant\nSport\nVacation\nEnter the category of the coupons you want to find from the list above.")
        if (userOption != null)
            userOption = userOption.toUpperCase()
        if (userOption === "ATTRACTION"
            || userOption === "AUTOMOBILE"
            || userOption === "ELECTRICITY"
            || userOption === "FOOD"
            || userOption === "GAMING"
            || userOption === "RESTAURANT"
            || userOption === "SPORT"
            || userOption === "VACATION") {
            CustomerService
                .getCouponsCategory(userOption).then(
                    result => {
                        props.setCoupons(result.data)
                    },
                    error => {
                        if (error.response.data.response) {
                            setTimeout(() => { alert("Action Failed\n" + error.response.data.response) }, 0)
                            return
                        }
                        if (error.response)
                            return
                        setTimeout(() => { alert("Servers are currently down, try again later.") }, 0)
                    }
                )
        }
    }

    const handleSearchPrice = () => {
        let userOption = prompt("Enter the maximum price of the coupons you want to find.")
        if (isNaN(userOption))
            alert('abc')
        else if (userOption < 0)
            alert("")
        else
            CustomerService
                .getCouponsPrice().then(
                    result => {
                        props.setCoupons(result.data)
                    },
                    error => {
                        if (error.response.data.response) {
                            setTimeout(() => { alert("Action Failed\n" + error.response.data.response) }, 0)
                            return
                        }
                        if (error.response)
                            return
                        setTimeout(() => { alert("Servers are currently down, try again later.") }, 0)
                    }
                )
    }

    return (
        <div className='CustomerCouponActionbar'>
            <div className='action-sidebar'>
                <button className='action-sidebar-button' onClick={handleAllCoupons} title='All Coupons'>All Coupons</button>
                <div className='action-sidebar-button-seperator'></div>
                <button className='action-sidebar-button' onClick={handleSearchPrice} title='Search Price'>Search Price</button>
                <div className='action-sidebar-button-seperator'></div>
                <button className='action-sidebar-button' onClick={handleSearchCategory} title='Search Category'>Search Category</button>
            </div>
        </div>
    )
}
