import './sidebar.css'
import image from './../../../assests/The Coupon Project Logo.png'
import { useRef } from 'react'

export default function Sidebar() {
    const buttons = {
        homePage: useRef(), homePageButton: useRef(),
        test: useRef(), testButton: useRef()
    }

    const buttonClick = (event) => {
        const buttonArray = JSON.parse(buttons)
        // for (let button of JSON.parse(buttons)) {
        //     console.log(button)
        // }
        switch (event.target.name) {
            case 'homePage':
                buttons.homePage.current.className = 'Sidebar-button-div-clicked'
                buttons.homePageButton.current.className = 'Sidebar-button-clicked'
                break;
            case 'test':
                buttons.test.current.className = 'Sidebar-button-div-clicked'
                buttons.testButton.current.className = 'Sidebar-button-clicked'
                break;
            default:
                window.location.reload()
                break;
        }
    }
    const imageClick = () => {
        alert('You clicked the logo')
    }
    return (<div className='Sidebar'>
        <img src={image} alt='logo' width='144' className='Sidebar-img' onClick={() => imageClick()} />
        <div className='Sidebar-img-div' onClick={() => imageClick()}></div>

        <div className='Siderbar-div-seperator'></div>
        <div className='Sidebar-button-div' ref={buttons.homePage}>
            <button className='Sidebar-button' name='homePage' ref={buttons.homePageButton} onClick={buttonClick} >Home Page</button>
        </div>

        <div className='Siderbar-div-button-seperator'></div>
        <div className='Sidebar-button-div' ref={buttons.test}>
            <button className='Sidebar-button' name='test' ref={buttons.testButton} onClick={buttonClick}>Test</button>
        </div>
    </div>)
}
