import './sidebar.css'
import image from './../../../assests/The Coupon Project Logo.png'
import { useRef } from 'react'

export default function Sidebar() {
    const [homePage, test] = [useRef(), useRef()]
    // const buttons = { homePage: useRef(), test: useRef() }

    const buttonClick = (event) => {
        switch (event.target.ref) {
            case homePage:
                console.log('a')
                break;
            case test:
                console.log('b')
                break;
            default:
                console.log('c')
                break;
        }
    }
    const imageClick = () => {
        alert('You clicked the logo')
    }
    return (<div className='Sidebar'>
        <img src={image} alt='logo' width='144' className='Sidebar-img' onClick={() => imageClick()} />
        <div className='Sidebar-img-div' onClick={() => imageClick()}></div>

        <div className='Siderbar-div-seperator'></div>
        <div className='Sidebar-button-div' ref={homePage} onClick={buttonClick}>
            <button className='Sidebar-button' >Home Page</button>
        </div>

        <div className='Siderbar-div-button-seperator'></div>
        <div className='Sidebar-button-div' ref={test} onClick={buttonClick}>
            <button className='Sidebar-button'>Test</button>
        </div>
    </div>)
}
