import axios from 'axios';


// axios.interceptors.request.use(config => {


//     config.headers.Authorization = 'Access-Control-Allow-Origin';


//     return config;
// });

class AuthenticationService {
    login = async (email, password, userType) => {
        return axios.post("http://localhost:8080/login-manager/login?email=" + email + "&password=" + password + "&userType=" + userType)
            .then(response => {
                if (response.data) {
                    console.log(response.data)
                    localStorage.setItem("user", JSON.stringify(response.data));
                }
                console.log(response)
                return response.data;
            })
            .catch(err => {
                throw err;
            });
    }

    logOut() {
        localStorage.removeItem("user");
    }

    //TODO here
    // registerCustomer = async (firstName, lastName, email, password) => {
    //     return axios.post("http://localhost:8080/admin/add-customer", {
    //         firstName, lastName, email, password
    //     });
    // }

    // registerCompany = async (name, email, password) => {
    //     return axios.post("http://localhost:8080/admin/create-company", {
    //         name, email, password
    //     });
    // }

    getCurrentUser() {
        return JSON.parse(localStorage.getItem('user'));
    }
}

export default new AuthenticationService();
