import './login.css'
import image from './../../../../assests/The Coupon Project Logo.png'
import * as Yup from "yup";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import history from '../../history';



export default function Login(props) {

    const getToken = async (values) => {
        //temporary
        if (true)
            return true

        const response = await fetch(
            'https://academeez-login-ex.herokuapp.com/api/users/login',
            {
                method: 'GET',
                body: JSON.stringify(values),
                headers: {
                    'Content-Type': 'application/json'
                }
            }
        )
        const json = await response.json();
        const token = json.token;
        props.setToken(token);
    }




    const handleSubmit = (values, { setFieldValue }) => {
        //if login is successful
        if (getToken()) {
            switch (values.type) {
                case 'Customer':
                    history.push('/customer/home')
                    break;
                case 'Company':
                    history.push('/company/home')
                    break;
                case 'Admin':
                    history.push('/admin/home')
                    break;
                default:
                    history.push('/login')
                    break;
            }
            return
        }
        //if login is unsuccessful
        setFieldValue('password', '')
        setTimeout(() => { alert("Email or Password are incorrect, try again.") }, 0)
    };

    const validationSchema = Yup.object({
        email: Yup.string().email('Invalid email format').required('Required'),
        password: Yup.string().required('Required')
    })

    return (<div className='Login'>
        <img src={image} alt='Login img' width='500' className='Login-img' />
        <div className='Login-formik'>
            <Formik initialValues={{ email: '', password: '', type: 'Customer' }}
                onSubmit={(values, { setFieldValue }) => handleSubmit(values, { setFieldValue })}
                validationSchema={validationSchema}
            >
                {({ isValid, dirty }) => (
                    <Form>
                        <div className="form-group" id='Login-padding'>
                            <label>
                                Enter Email:
                            </label>
                            <Field
                                type='email'
                                name='email'
                                className='form-control'
                                placeholder='Email'
                                autoComplete='true'
                            />
                        </div>
                        <ErrorMessage component='div' name='email' className='alert alert-primary' id='Login-alert' />
                        <div className="form-group" id='Login-padding'>
                            <label>
                                Enter Password:
                            </label>
                            <Field
                                type='password'
                                name='password'
                                className='form-control'
                                placeholder='Password'
                                autoComplete='true'
                            />
                        </div>
                        <div className='Login-alerts'>
                            <ErrorMessage component='div' name='password' className='alert alert-primary' id='Login-alert' />
                        </div>
                        <div className="form-group" id='Login-padding'>
                            <label>
                                Login as...
                            </label>
                            <Field
                                className='form-select'
                                component="select"
                                name="type"
                            >
                                <option value="Customer">Customer</option>
                                <option value="Company">Company</option>
                                <option value="Admin">Admin</option>
                            </Field>
                        </div>
                        <div className='Login-submit' id='Login-padding'>
                            <button type='submit' className='btn btn-primary' disabled={!(isValid && dirty)} >
                                Login
                            </button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
    </div >)
}
