import { useState } from 'react';
import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import Sidebar from './Sidebar/Sidebar';
import Customer from './content/Customer/Customer';
import Company from './content/Company/Company';
import Admin from './content/Admin/Admin';



function App() {
  const [content, setContent] = useState('Admin')
  const [[headerVisible, setHeaderVisible], [sidebarVisible, setSidebarVisible]] = [useState(false), useState(false)]
  const [sendToContent, setSendToContent] = useState('Home')

  const getContent = () => {
    switch (content) {
      case 'Login':
        return <Login
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
        />
      case 'Customer':
        return <Customer
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
          respondContent={sendToContent}
        />
      case 'Company':
        return <Company
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
          respondContent={sendToContent}
        />
      case 'Admin':
        return <Admin
          setContent={setContent}
          setSidebarVisible={setSidebarVisible}
          setHeaderVisible={setHeaderVisible}
          respondContent={sendToContent}
        />
      default:
        window.location.reload()
        break;
    }
  }
  const getSidebar = () => {
    if (sidebarVisible)
      return <Sidebar setSendToContent={setSendToContent} />
  }
  const getHeader = () => {
    if (headerVisible)
      return <Header setSendToContent={setSendToContent} />
  }

  return (

    <div className="App">
      <div className="App-Header">
        {getHeader()}
      </div>
      <div className="App-Sidebar">
        {getSidebar()}
      </div>
      <div className="App-Content">
        {getContent()}
      </div>
      <div className="App-Footer">
        <Footer currentContent={content} />
      </div>
    </div>
  );
}

export default App;
