import { Component, useState } from 'react';
import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import CustomerSidebar from './user-sidebar/CustomerSidebar/CustomerSidebar';
import CompanySidebar from './user-sidebar/CompanySidebar/CompanySidebar';
import AdminSidebar from './user-sidebar/AdminSidebar/AdminSidebar';
import User from './content/User/User';
import { BrowserRouter as Router, Link, Redirect, Route, Switch, Router as HistoryRouter } from 'react-router-dom'
import { Button } from 'semantic-ui-react'

import history from './history';

import Temp from './Temp'

function App() {
  const [content, setContent] = useState('Login')
  const [[headerVisible, setHeaderVisible], [sidebarVisible, setSidebarVisible]] = [useState(false), useState(false)]
  const [sendToContent, setSendToContent] = useState('Home')
  // const [token, setToken] = useState('') use later for tokens

  const getContent = () => {
    return (
      <Switch>
        <Route path={["/customer", "/company", "/admin"]}>
          <User />
        </Route>
        <Route path='/'>
          <Login />
        </Route>
      </Switch>
    )
  }

  const getSidebar = () => {
    return (
      <Switch>
        <Route path='/customer'>
          <CustomerSidebar />
        </Route>
        <Route path='/company'>
          <CompanySidebar />
        </Route>
        <Route path='/admin'>
          <AdminSidebar />
        </Route>
      </Switch>
    )
  }
  const getHeader = () => {
    return (
      <Route path={["/customer", "/company", "/admin"]}>
        <Header />
      </Route>
    )
  }

  //wrapping Link in button
  //   <Link>
  //   <Button to='/hello' className='form-control'>Home</Button>
  // </Link>

  return (
    // <Router>
    //   <div className="App">
    //     <div className="App-Header">
    //       {getHeader()}
    //     </div>
    //     <div className="App-Sidebar">
    //       {getSidebar()}
    //     </div>
    //     <div className="App-Content">
    //       {getContent()}
    //     </div>
    //     <div className="App-Footer">
    //       <Footer currentContent={content} />
    //     </div>
    //   </div>
    // </Router >
    <HistoryRouter history={history}>
      <Temp />
    </HistoryRouter>
  );
}

export default App;
