import './login.css'
import image from './../../../../assests/The Coupon Project Logo.png'
import * as Yup from "yup";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import { useEffect, useRef, useState } from 'react';
import ForceUpdate from '../../../../hooks/ForceUpdate';

export default function Login(props) {
    const [submitRef, formikRef] = [useRef(), useRef()]

    useEffect(() => {
        props.setSidebarVisible(false)
        props.setHeaderVisible(false)
    })

    const validationSchema = Yup.object({
        email: Yup.string().email().required('Required'),
        password: Yup.string().required('Required')
    })

    const handleSubmit = (values, { setFieldValue }) => {
        //if login is successful
        // if (true) {
        //     switch (values.type) {
        //         case 'Customer':
        //             props.setContent('Customer')
        //             break;
        //         case 'Company':
        //             props.setContent('Company')
        //             break;
        //         case 'Admin':
        //             props.setContent('Admin')
        //             break;
        //         default:
        //             window.location.reload()
        //             break;
        //     }
        //     return
        // }
        //if login is unsuccessful
        setFieldValue('password', '')
        setTimeout(() => { alert("Email or Password are incorrect, try again.") }, 0)
    };

    return (<div className='Login'>
        <img src={image} alt='Login img' width='500' className='Login-img' />
        <div className='Login-formik'>
            <Formik initialValues={{ email: '', password: '', type: 'Customer' }}
                onSubmit={(values, { setFieldValue }) => handleSubmit(values, { setFieldValue })}
                validationSchema={validationSchema}
                innerRef={formikRef}
            >
                {({ handleChange, isValid, dirty }) => (
                    <Form>
                        <div className="form-group" id='Login-padding'>
                            <label>
                                Enter Email:
                            </label>
                            <Field
                                type='email'
                                name='email'
                                className='form-control'
                                placeholder='Email'
                                autoComplete='true'
                            />
                        </div>
                        <ErrorMessage component='div' name='email' className='alert alert-primary' id='Login-alert' />
                        <div className="form-group" id='Login-padding'>
                            <label>
                                Enter Password:
                            </label>
                            <Field
                                type='password'
                                name='password'
                                className='form-control'
                                placeholder='Password'
                                autoComplete='true'
                            />
                        </div>
                        <div className='Login-alerts'>
                            <ErrorMessage component='div' name='password' className='alert alert-primary' id='Login-alert' />
                        </div>
                        <div className="form-group" id='Login-padding'>
                            <label>
                                Login as...
                            </label>
                            <Field
                                className='form-select'
                                component="select"
                                name="type"
                            >
                                <option value="Customer">Customer</option>
                                <option value="Company">Company</option>
                                <option value="Admin">Admin</option>
                            </Field>
                        </div>
                        <div className='Login-submit' id='Login-padding'>
                            <button type='submit' className='btn btn-primary' ref={submitRef} disabled={!(isValid && dirty)} >
                                Login
                            </button>
                        </div>
                    </Form>
                )}
            </Formik>

        </div>

    </div >)
}
