import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import CustomerSidebar from './user-sidebar/CustomerSidebar/CustomerSidebar';
import CompanySidebar from './user-sidebar/CompanySidebar/CompanySidebar';

import User from './content/User/User';
import { Route, Switch, Router, Redirect } from 'react-router-dom'
import history from './history';
import React, { Suspense, useState } from 'react';
const AdminSidebar = React.lazy(() => import('./user-sidebar/AdminSidebar/AdminSidebar'))
function App() {
  const [token, setToken] = useState(null)

  const getContent = () => {
    return (
      <Switch>
        <Route path={["/customer", "/company", "/admin"]}>
          <User token={token} />
        </Route>
        <Route>
          <Redirect to='/login' setToken={setToken} />
          <Login />
        </Route>
      </Switch>
    )
  }

  const getSidebar = () => {
    return (
      <Switch>
        <Route path='/customer'>
          <CustomerSidebar />
        </Route>
        <Route path='/company'>
          <CompanySidebar />
        </Route>
        <Route path='/admin'>
          <Suspense fallback={<div>Loading...</div>}>
            <AdminSidebar />
          </Suspense>
        </Route>
      </Switch>
    )
  }
  const getHeader = () => {
    return (
      <Route path={["/customer", "/company", "/admin"]}>
        <Header setToken={setToken} />
      </Route>
    )
  }

  return (
    <Router history={history}>
      <div className="App">

        {getHeader()}
        {getSidebar()}

        {getContent()}
        <Footer />


      </div>
    </Router >
  );
}

export default App;
