import { useEffect, useRef } from 'react'
import './customer.css'
import './../../../../styles/user.css'

export default function Customer(props) {

    const customerRef = useRef()

    useEffect(() => {
        props.setSidebarVisible(true)
        props.setHeaderVisible(true)
        switch (props.respondContent) {
            case 'Home':
                customerRef.current.id = ''
                break;
            case 'Test':
                customerRef.current.id = 'Customer-opacity'
                break;
            default:
                break;
        }
    })

    return (<div className='Customer' ref={customerRef}>

    </div>)
}
