import './login.css'
import image from './../../../../assests/The Coupon Project Logo.png'
import * as Yup from "yup";
import { Formik, Form, Field, ErrorMessage } from 'formik';
import history from '../../history';

export default function Login(props) {
    const getToken = async (values) => {
        //temporary
        if (true)
            return true



        // AuthenticationService
        //     .signin(this.state.username,
        //         this.state.password)
        //     .then(
        //         () => {
        //             this.props.history.push('/profile');
        //         },
        //         error => {
        //             console.log("Login fail: error = { " + error.toString() + " }");
        //             this.setState({ error: "Can not signin successfully ! Please check username/password again" });
        //         }
        //     );
    }

    // UPDATED LOGIN 
    // doLogin = async (event) => {
    //     event.preventDefault();

    //     AuthenticationService
    //         .signin(this.state.username, 
    //                   this.state.password)
    //       .then(
    //         () => {
    //           this.props.history.push('/profile');
    //         },
    //         error => {
    //           console.log("Login fail: error = { " + error.toString() + " }");
    //           this.setState({error: "Can not signin successfully ! Please check username/password again"});
    //         }
    //     );
    //   }

    const handleSubmit = (values, { setFieldValue }) => {
        //if login is successful
        if (getToken(values)) {
            switch (values.type) {
                case 'Customer':
                    history.push('/customer/home')
                    break;
                case 'Company':
                    history.push('/company/home')
                    break;
                case 'Admin':
                    history.push('/admin/home')
                    break;
                default:
                    history.push('/login')
                    break;
            }
            return
        }
        //if login is unsuccessful
        setFieldValue('password', '')
        setTimeout(() => { alert("Email or Password are incorrect, try again.") }, 0)
    };

    const validationSchema = Yup.object({
        email: Yup.string().email('Invalid email format').required('Required'),
        password: Yup.string().required('Required')
    })

    return (<div className='Login'>
        <img src={image} alt='Login img' width='26%' className='Login-img' />
        <div className='Login-formik'>
            <Formik initialValues={{ email: '', password: '', type: 'Customer' }}
                onSubmit={(values, { setFieldValue }) => handleSubmit(values, { setFieldValue })}
                validationSchema={validationSchema}
            >
                {({ isValid, dirty }) => (
                    <Form>
                        <div className="form-group" id='Login-padding'>
                            <label className='Login-label'>
                                Enter Email:
                            </label>
                            <Field
                                type='email'
                                name='email'
                                className='form-control'
                                placeholder='Email'
                                autoComplete='true'
                                id='Login-input'
                            />
                        </div>
                        <ErrorMessage component='div' name='email' className='alert alert-primary' id='Login-alert' />
                        <div className="form-group" id='Login-padding'>
                            <label className='Login-label'>
                                Enter Password:
                            </label>
                            <Field
                                type='password'
                                name='password'
                                className='form-control'
                                placeholder='Password'
                                autoComplete='true'
                                id='Login-input'
                            />
                        </div>
                        <div className='Login-alerts'>
                            <ErrorMessage component='div' name='password' className='alert alert-primary' id='Login-alert' />
                        </div>
                        <div className="form-group" id='Login-padding'>
                            <label className='Login-label'>
                                Login as...
                            </label>
                            <Field
                                className='form-select'
                                component="select"
                                name="type"
                                id='Login-input'
                            >
                                <option value="Customer">Customer</option>
                                <option value="Company">Company</option>
                                <option value="Admin">Admin</option>
                            </Field>
                        </div>
                        <div className='Login-submit' id='Login-padding'>
                            <button type='submit' className='btn btn-primary' id='Login-input' disabled={!(isValid && dirty)} >
                                Login
                            </button>
                        </div>
                    </Form>
                )}
            </Formik>
        </div>
    </div >)
}
