import './app.css';
import Footer from './Footer/Footer';
import Login from './content/Login/Login';
import Header from './Header/Header';
import User from './content/User/User';
import { Route, Switch, Router, Redirect } from 'react-router-dom'
import history from './history';
import { Suspense, lazy } from 'react';
import Idle from 'react-idle'
import './../../styles/loading-div.css'
const AdminSidebar = lazy(() => import('./user-sidebar/AdminSidebar/AdminSidebar'))
const CompanySidebar = lazy(() => import('./user-sidebar/CompanySidebar/CompanySidebar'))
const CustomerSidebar = lazy(() => import('./user-sidebar/CustomerSidebar/CustomerSidebar'))

function App() {


  <Idle
    timeout={5000}
    render={({ idle }) =>
      <h1>
        {idle
          ? "You are idle."
          : "Stop doing stuff for 2 seconds."
        }
      </h1>
    }
  />

  const getContent = () => {
    return (
      <Switch>
        <Route path={["/customer", "/company", "/admin"]}>
          <User />
        </Route>
        <Route>
          <Redirect to='/login' />
          <Login />
        </Route>
      </Switch>
    )
  }

  const getSidebar = () => {
    return (
      <Suspense fallback={<div className='loading-div'></div>}>
        <Switch>
          <Route path='/customer'>
            <CustomerSidebar />
          </Route>
          <Route path='/company'>
            <CompanySidebar />
          </Route>
          <Route path='/admin'>
            <AdminSidebar />
          </Route>
        </Switch>
      </Suspense>
    )
  }
  const getHeader = () => {
    return (
      <Route path={["/customer", "/company", "/admin"]}>
        <Header />
      </Route>
    )
  }

  return (
    <Router history={history}>
      <div className="App">
        {getHeader()}
        {getSidebar()}
        {getContent()}
        <Footer />
      </div>
    </Router >
  );
}

export default App;
