import './sidebar.css'
import image from './../../../assests/The Coupon Project Logo.png'
import { useRef } from 'react'

export default function Sidebar() {
    const [test, testButton] = [useRef(), useRef()]
    const [homePage, homePageButton] = [useRef(), useRef()]
    // const buttons = { homePage: useRef(), test: useRef() }

    const buttonClick = (event) => {

        switch (event.target.name) {
            case 'homePage':
                homePage.current.className = 'Sidebar-button-div-clicked'
                homePageButton.current.className = 'Sidebar-button-clicked'
                console.log('a')
                break;
            case 'test':
                console.log('b')
                break;
            default:
                window.location.reload()
                break;
        }
    }
    const imageClick = () => {
        alert('You clicked the logo')
    }
    return (<div className='Sidebar'>
        <img src={image} alt='logo' width='144' className='Sidebar-img' onClick={() => imageClick()} />
        <div className='Sidebar-img-div' onClick={() => imageClick()}></div>

        <div className='Siderbar-div-seperator'></div>
        <div className='Sidebar-button-div' ref={homePage}>
            <button className='Sidebar-button' name='homePage' ref={homePageButton} onClick={buttonClick} >Home Page</button>
        </div>

        <div className='Siderbar-div-button-seperator'></div>
        <div className='Sidebar-button-div'>
            <button className='Sidebar-button' name='test' ref={test} onClick={buttonClick}>Test</button>
        </div>
    </div>)
}
