                            <td htmlFor={stream.id}><label htmlFor={stream.id} className="CouponsCustomer-table-text-label" title={`RELEASE DATE: ${stream.startDate}`}>{stream.startDate}</label></td>
                            <td htmlFor={stream.id}><label htmlFor={stream.id} className="CouponsCustomer-table-text-label" title={`EXPIRATION DATE: ${stream.endDate}`}>{stream.endDate}</label></td>
