import './header.css'
import history from '../history'
import { Route, Switch } from 'react-router-dom'

export default function Header(props) {
    const logout = () => {
        props.setToken(null)
        history.push('/login')
    }

    const profile = () => {
        if (history.location.pathname.startsWith('/customer'))
            history.push('/customer/profile')
        if (history.location.pathname.startsWith('/company'))
            history.push('/company/profile')
    }

    const getProfile = () => {
        return (
            <div className='Header-button-div'>
                <button className='Header-logout' onClick={() => logout()}>Logout</button>
                <Route path='/customer'>
                    <button className='Header-profile' onClick={() => profile()}>Profile</button>
                </Route>
            </div>
        )
    }

    // const getProfile = () => {
    //     return (
    //         <Switch>
    //             <Route path='/admin'>
    //                 <div className='Header-button-div'>
    //                     <button className='Header-logout' onClick={() => logout()}>Logout</button>
    //                 </div>
    //             </Route>
    //             <Route>
    //                 <div className='Header-button-div'>
    //                     <button className='Header-profile' onClick={() => profile()}>Profile</button>
    //                     <button className='Header-logout' onClick={() => logout()}>Logout</button>
    //                 </div>
    //             </Route>
    //         </Switch >
    //     )
    // }

    return (<div className='Header'>
        {getProfile()}
    </div>)
}
