import './header.css'
import history from '../history'
import { Route } from 'react-router-dom'
import AuthenticationService from './../../../services/AuthenticationService'
import Idle from 'react-idle'

export default function Header() {

    <Idle
        timeout={5000}
        render={({ idle }) =>
            <h1>
                {idle
                    ? "You are idle."
                    : "Stop doing stuff for 2 seconds."
                }
            </h1>
        }
    />


    const logout = () => {
        AuthenticationService.logOut();
        history.push('/login');
        window.location.reload();
    }

    const profile = () => {
        if (history.location.pathname.startsWith('/customer'))
            history.push('/customer/profile')
        if (history.location.pathname.startsWith('/company'))
            history.push('/company/profile')
    }

    const getProfile = () => {
        return (
            <div className='Header-button-div'>
                <Route path={['/customer', '/company']}>
                    <button className='Header-profile' onClick={() => profile()} title='Profile'>Profile</button>
                </Route>
                <button className='Header-logout' onClick={() => logout()} title='Logout'>Logout</button>
            </div>
        )
    }

    return (<div className='Header'>
        {getProfile()}
    </div>)
}
