import { useEffect, useRef } from 'react'
import './admin.css'
import './../../../../styles/user.css'

export default function Admin(props) {

    const adminRef = useRef()

    useEffect(() => {
        props.setSidebarVisible(true)
        props.setHeaderVisible(true)
        switch (props.respondContent) {
            case 'Home':
                adminRef.current.id = ''
                break;
            case 'Test':
                adminRef.current.id = 'Customer-opacity'
                break;
            default:
                break;
        }
    })

    return (<div className='Admin' ref={adminRef}>

    </div>)
}
